const Menu = () => {
  const [selectedCategory, setSelectedCategory] = useState('cakes');
  const [items, setItems] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [userDetails, setUserDetails] = useState({
    name: '',
    contact: '',
    address: '',
    quantity: 1, // Add quantity field
  });
  const [orderConfirmation, setOrderConfirmation] = useState('');
  
  const navigate = useNavigate(); // Initialize useNavigate

  useEffect(() => {
    const updatedItems = menuData[selectedCategory].map((item) => ({
      ...item,
      image: imageMap[item.imageId],
    }));
    setItems(updatedItems);
  }, [selectedCategory]);

  const handleOrderClick = (item) => {
    setSelectedItem(item);
    setShowForm(true);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserDetails((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const orderDetails = {
      ...userDetails,
      item: selectedItem,
    };
    navigate('/ordered', { state: orderDetails }); // Navigate to Ordered page with state
    setShowForm(false);
    setUserDetails({
      name: '',
      contact: '',
      address: '',
      quantity: 1, // Reset quantity to 1
    });
  };

  return (
    <div className="menu-container">
      <h1>Our Menu</h1>
      <div className="category-buttons">
        {Object.keys(menuData).map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`category-button ${category === selectedCategory ? 'active' : ''}`}
          >
            {category.charAt(0).toUpperCase() + category.slice(1)}
          </button>
        ))}
      </div>

      <div className="menu-items">
        {items.map((item) => (
          <div key={item.id} className="menu-item-card">
            <img src={item.image} alt={item.name} />
            <h3 className="menu-item-name">{item.name}</h3>
            <p className="menu-item-description">{item.description}</p>
            <p className="menu-item-price">${item.price.toFixed(2)}</p>
            <button className="order-button" onClick={() => handleOrderClick(item)}>
              Order Now
            </button>
          </div>
        ))}
      </div>

      {showForm && (
        <div className="order-form-overlay">
          <div className="order-form">
            <h2>Order Form</h2>
            <form onSubmit={handleFormSubmit}>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  value={userDetails.name}
                  onChange={handleInputChange}
                  required
                />
              </label>
              <label>
                Contact Number:
                <input
                  type="text"
                  name="contact"
                  value={userDetails.contact}
                  onChange={handleInputChange}
                  required
                />
              </label>
              <label>
                Address:
                <textarea
                  name="address"
                  value={userDetails.address}
                  onChange={handleInputChange}
                  required
                />
              </label>
              <label>
                Quantity:
                <input
                  type="number"
                  name="quantity"
                  value={userDetails.quantity}
                  onChange={handleInputChange}
                  min="1"
                  required
                />
              </label>
              <button type="submit" className="submit-order-button">
                Order
              </button>
            </form>
            <button className="close-form-button" onClick={() => setShowForm(false)}>
              Close
            </button>
          </div>
        </div>
      )}

      {orderConfirmation && (
        <div className="order-confirmation-message">{orderConfirmation}</div>
      )}
    </div>
  );
};
