// src/pages/Menu.js
import React, { useState, useEffect } from 'react';
import './menu.css';
import menuCategories from './menuCategories.json';

const Menu = () => {
  const [selectedCategory, setSelectedCategory] = useState('cakes');
  const [items, setItems] = useState([]);

  useEffect(() => {
    setItems(menuCategories[selectedCategory]);
  }, [selectedCategory]);

  return (
    <div className="menu-container">
      <h2 className="menu-title">Our Menu</h2>
      <div className="category-buttons">
        {Object.keys(menuCategories).map((category) => (
          <button
            key={category}
            className={`category-button ${
              selectedCategory === category ? 'active' : ''
            }`}
            onClick={() => setSelectedCategory(category)}
          >
            {category.charAt(0).toUpperCase() + category.slice(1)}
          </button>
        ))}
      </div>

      <div className="menu-items">
        {items.map((item) => (
          <div
            key={item.id}
            className="menu-item"
            onClick={() => alert(`Clicked on ${item.name}`)} // Replace with navigation
          >
            <img src={item.image} alt={item.name} className="menu-item-image" />
            <div className="menu-item-info">
              <h3 className="menu-item-name">{item.name}</h3>
              <p className="menu-item-description">{item.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
