// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Loader from './components/Loader'; // Import the Loader component
import Home from './pages/Home';
import Menu from './pages/Menu';
import Cart from './pages/Cart';
import Contact from './pages/Contact';
import About from './pages/About';
import NotFound from './pages/NotFound';

const App = () => {
  const [loading, setLoading] = useState(true); // State to manage loading screen

  // Simulate loading delay
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false); // Hide loader after 3 seconds
    }, 3000);

    return () => clearTimeout(timer); // Cleanup timer on unmount
  }, []);

  // If loading is true, show the Loader component
  if (loading) {
    return <Loader />;
  }

  return (
    <Router>
      <div className="App">
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} /> {/* Home Page */}
          <Route path="/menu" element={<Menu />} /> {/* Menu Page */}
          <Route path="/cart" element={<Cart />} /> {/* Cart Page */}
          <Route path="/contact" element={<Contact />} /> {/* Contact Page */}
          <Route path="/about" element={<About />} /> {/* About Us Page */}
          <Route path="*" element={<NotFound />} /> {/* 404 Not Found */}
        </Routes>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
