import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import '../css/ordered.css';

const Ordered = () => {
  const location = useLocation();
  const { selectedItem, quantity } = location.state || { selectedItem: null, quantity: 1 };

  const [previousOrders, setPreviousOrders] = useState(() => {
    const savedOrders = localStorage.getItem('previousOrders');
    return savedOrders ? JSON.parse(savedOrders) : [];
  });

  useEffect(() => {
    if (selectedItem) {
      const alreadyOrdered = previousOrders.find(item => item.id === selectedItem.id);

      // If not already ordered, add the new order
      if (!alreadyOrdered) {
        const newOrder = { ...selectedItem, quantity };
        const updatedOrders = [...previousOrders, newOrder];
        setPreviousOrders(updatedOrders);
        localStorage.setItem('previousOrders', JSON.stringify(updatedOrders)); // Save to local storage
      }
    }
  }, [selectedItem, quantity, previousOrders]);

  const handleRemoveOrder = (index) => {
    // Remove the order from the previousOrders array
    const updatedOrders = previousOrders.filter((_, i) => i !== index);
    
    // Update state and local storage
    setPreviousOrders(updatedOrders);
    localStorage.setItem('previousOrders', JSON.stringify(updatedOrders));
  };

  return (
    <div className="ordered-page">
      <h1 className="ordered-title">Your Orders</h1>

      {previousOrders.length === 0 ? (
        <p>No orders placed yet.</p>
      ) : (
        <div className="ordered-container">
          {previousOrders.map((item, index) => (
            <div key={index} className="ordered-item">
              <img src={item.image} alt={item.name} />
              <h2>{item.name}</h2>
              <p>Description: {item.description}</p>
              <p>Price: ${item.price.toFixed(2)}</p>
              <p>Quantity: {item.quantity}</p>
              <button className="shipped-button" onClick={() => handleRemoveOrder(index)}>
                Shipped
              </button>
            </div>
          ))}
        </div>
      )}
      
      <Link to="/menu" className="back-to-menu">Back to Menu</Link>
    </div>
  );
};

export default Ordered;
