import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import Menu from './pages/Menu';
import Ordered from './pages/Ordered'; // Import the Ordered component
import Contact from './pages/Contact';
import About from './pages/About';
import NotFound from './pages/NotFound';

const App = () => {
  const [previousOrders, setPreviousOrders] = useState([]); // State to manage previous orders

  return (
    <Router>
      <div className="App">
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/menu" element={<Menu setPreviousOrders={setPreviousOrders} />} /> {/* Pass setPreviousOrders to Menu */}
          <Route path="/orders" element={<Ordered previousOrders={previousOrders} setPreviousOrders={setPreviousOrders} />} /> {/* Pass orders and setter */}
          <Route path="/contact" element={<Contact />} />
          <Route path="/about" element={<About />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
