// src/pages/Ordered.js
import React from 'react';
import '..//ordered.css';

const Ordered = ({ previousOrders, setPreviousOrders }) => {
  
  const handleRemoveOrder = (index) => {
    const newOrders = [...previousOrders];
    newOrders.splice(index, 1); // Remove the order at the specified index
    setPreviousOrders(newOrders); // Update state
  };

  return (
    <div className="ordered-container">
      <h1>Your Orders</h1>
      
      {previousOrders.length === 0 ? (
        <p>No orders placed yet.</p>
      ) : (
        previousOrders.map((item, index) => (
          <div key={index} className="ordered-item">
            <img src={item.image} alt={item.name} />
            <h2>{item.name}</h2>
            <p>Description: {item.description}</p>
            <p>Price: ${item.price.toFixed(2)}</p>
            <p>Quantity: {item.quantity}</p>
            <button className="shipped-button" onClick={() => handleRemoveOrder(index)}>
              Shipped
            </button>
          </div>
        ))
      )}
      
      <button>
        <a href="/menu" style={{ textDecoration: 'none', color: 'white' }}>
          Back to Menu
        </a>
      </button>
    </div>
  );
};

export default Ordered;
