// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Loader from './components/Loader'; // Import the Loader component
import Home from './pages/Home';
import Menu from './pages/Menu';
import Contact from './pages/Contact';
import About from './pages/About';
import NotFound from './pages/NotFound';
import Service from './pages/Service';
import Review from './pages/Review';
import Ordered from './pages/Ordered'; // Import the Ordered page

const App = () => {
  const [loading, setLoading] = useState(true); // State to manage loading screen
  const [previousOrders, setPreviousOrders] = useState([]); // Define previousOrders state

  // Simulate loading delay
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false); // Hide loader after 3 seconds
    }, 1500);

    return () => clearTimeout(timer); // Cleanup timer on unmount
  }, []);

  // If loading is true, show the Loader component
  if (loading) {
    return <Loader />;
  }

  return (
    <Router>
      <div className="App">
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} /> {/* Home Page */}
          <Route 
            path="/menu" 
            element={<Menu setPreviousOrders={setPreviousOrders} />} 
          /> {/* Menu Page */}
          <Route 
            path="/orders" 
            element={<Ordered previousOrders={previousOrders} setPreviousOrders={setPreviousOrders} />} 
          /> {/* Ordered Page */}
          <Route path="/service" element={<Service />} /> {/* Service Page */}
          <Route path="/contact" element={<Contact />} /> {/* Contact Page */}
          <Route path="/about" element={<About />} /> {/* About Us Page */}
          <Route path="/review" element={<Review />} /> {/* Review Us Page */}
          <Route path="*" element={<NotFound />} /> {/* 404 Not Found */}
        </Routes>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
