// src/components/Loader.js
import React from 'react';
import '../css/loader.css';

const Loader = () => {
  return (
    <div className="loader-container">
      {/* Embedding the Tenor GIF */}
      <div className="tenor-gif-embed" data-postid="24130764" data-share-method="host" data-aspect-ratio="1" data-width="100%">
        <a href="https://tenor.com/view/cupcake-cute-dancing-gif-24130764">Cupcake Cute GIF</a> from <a href="https://tenor.com/search/cupcake-gifs">Cupcake GIFs</a>
      </div>

      {/* Loading bar */}
      <div className="loading-bar">
        <div className="loading-progress"></div>
      </div>

      {/* Embed script */}
      <script type="text/javascript" async src="https://tenor.com/embed.js"></script>
    </div>
  );
};

export default Loader;
